<?php require 'inc/koneksi.php'; require 'inc/auth.php'; $id=intval($_GET['id']??0); $r=mysqli_query($koneksi,'SELECT * FROM inventaris WHERE id='.$id); if($r && mysqli_num_rows($r)){ $row=mysqli_fetch_assoc($r); if(!empty($row['gambar']) && file_exists('uploads/'.$row['gambar'])) @unlink('uploads/'.$row['gambar']); mysqli_query($koneksi,'DELETE FROM inventaris WHERE id='.$id); } header('Location: index.php'); exit; ?>